<div class="rvw-bars-row">

  <div class="rvw-bar-item">
    <canvas id="rvw-A-canvas" width="80" height="400" class="rvw-canvas"></canvas> <div id="rvw-A-label" class="rvw-bar-label"></div>
  </div>

  <div class="rvw-bar-item">
    <canvas id="rvw-B-canvas" width="80" height="400" class="rvw-canvas"></canvas> <div id="rvw-B-label" class="rvw-bar-label"></div>
  </div>

  <div class="rvw-bar-item" style="margin-left: 20px;">
    <canvas id="rvw-A_eq-canvas" width="80" height="400" class="rvw-canvas"></canvas> <div id="rvw-A_eq-label" class="rvw-bar-label"></div>
  </div>

  <div class="rvw-bar-item">
    <canvas id="rvw-B_eq-canvas" width="80" height="400" class="rvw-canvas"></canvas> <div id="rvw-B_eq-label" class="rvw-bar-label"></div>
  </div>

  <div class="rvw-result-group" style="margin-left: 20px;">
    <div class="rvw-bar-item" id="rvw-R1-item">
      <canvas id="rvw-R1" width="80" height="400" class="rvw-canvas"></canvas> <div id="rvw-R1-label" class="rvw-bar-label"></div>
    </div>
